package com.example.assetmanagement.util;

import com.example.assetmanagement.dto.AssetDto;
import com.example.assetmanagement.entity.Asset;
import com.example.assetmanagement.entity.User;

public class AssetMapper {

    public static AssetDto toDto(Asset asset) {
        if (asset == null) {
            return null;
        }

        AssetDto assetDto = new AssetDto();
        assetDto.setAsset_id(asset.getAsset_id());
        assetDto.setAsset_name(asset.getAsset_name());
        assetDto.setStatus(asset.getStatus());
        assetDto.setQuantity(asset.getQuantity());
        if (asset.getUser() != null) {
            assetDto.setUserId(asset.getUser().getId());
        }

        return assetDto;
    }

    public static Asset toEntity(AssetDto assetDto) {
        if (assetDto == null) {
            return null;
        }

        Asset asset = new Asset();
        asset.setAsset_id(assetDto.getAsset_id());
        asset.setAsset_name(assetDto.getAsset_name());
        asset.setStatus(assetDto.getStatus());
        asset.setQuantity(assetDto.getQuantity());

        if (assetDto.getUserId() != null) {
            User user = new User();
            user.setId(assetDto.getUserId());
            asset.setUser(user);
        }

        return asset;
    }
}

