import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// Import Styles
import 'bootstrap/dist/css/bootstrap.min.css';

// Import Context, Layouts, and Pages
import { AuthProvider, useAuth } from './context/AuthContext';
import MainLayout from './layouts/MainLayout';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import EmployeeDashboard from './pages/EmployeeDashboard';
import AdminDashboard from './pages/AdminDashboard';
import UserProfilePage from './pages/UserProfilePage';

// Component to handle redirection from the root path
const HomeRedirect = () => {
    const { isAuthenticated, user, loading } = useAuth();

    if (loading) {
        // Optional: show a loading spinner while auth state is being determined
        return <div>Loading...</div>;
    }

    if (!isAuthenticated) {
        return <Navigate to="/login" />;
    }

    if (user?.role === 'ROLE_ADMIN') {
        return <Navigate to="/admin-dashboard" />;
    } else if (user?.role === 'ROLE_EMPLOYEE') {
        return <Navigate to="/employee-dashboard" />;
    } else {
        // Fallback to login if role is unknown or user is not authenticated
        return <Navigate to="/login" />;
    }
};


// A component to protect routes
const PrivateRoute = ({ children, role }) => {
    const { isAuthenticated, user, loading } = useAuth();

    if (loading) {
        return <div>Loading...</div>; // Or a spinner component
    }

    if (!isAuthenticated) {
        return <Navigate to="/login" />;
    }

    // If a specific role is required and the user's role doesn't match
    if (role && user?.role !== role) {
        // Redirect them to their own dashboard, not the one they tried to access
        const dashboardPath = user?.role === 'ROLE_ADMIN' ? '/admin-dashboard' : '/employee-dashboard';
        return <Navigate to={dashboardPath} />;
    }

    return children;
};

const AppRoutes = () => (
    <Routes>
        {/* Public auth routes */}
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />

        {/* Protected Routes */}
        <Route
            path="/employee-dashboard"
            element={<PrivateRoute role="ROLE_EMPLOYEE"><EmployeeDashboard /></PrivateRoute>}
        />
        <Route
            path="/admin-dashboard"
            element={<PrivateRoute role="ROLE_ADMIN"><AdminDashboard /></PrivateRoute>}
        />
        <Route
            path="/profile"
            element={<PrivateRoute><UserProfilePage /></PrivateRoute>}
        />

        {/* Default route redirects based on auth status */}
        <Route
            path="/"
            element={<HomeRedirect />}
        />
    </Routes>
);

const App = () => {
    return (
        <AuthProvider>
            <Router>
                <AppRoutes />
            </Router>
        </AuthProvider>
    );
};

export default App;

import React, { useState, useEffect } from 'react';
import MainLayout from '../layouts/MainLayout';
import StatsCard from '../components/StatsCard';
import { apiService } from '../services/apiService';
import { Link } from 'react-router-dom';

const AdminDashboard = () => {
    const [stats, setStats] = useState({ totalAssets: 0, totalEmployees: 0, pendingRequests: 0, assetsUnderService: 0 });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchStats = async () => {
            try {
                // This assumes you have a backend endpoint like '/api/admin/stats'
                const data = await apiService.get('admin/assets');
                setStats(data);
            } catch (err) {
                setError('Failed to fetch dashboard stats.');
            } finally {
                setLoading(false);
            }
        };

        fetchStats();
    }, []);

    if (loading) {
        return <MainLayout><div>Loading dashboard...</div></MainLayout>;
    }

    if (error) {
        return <MainLayout><div className="alert alert-danger">{error}</div></MainLayout>;
    }

    return (
        <MainLayout>
            <div className="container-fluid">
                <h2 className="mb-4">Admin Dashboard</h2>

                <div className="row">
                    <StatsCard title="Total Assets" value={stats.totalAssets} icon="box-seam" color="primary" />
                    <StatsCard title="Total Employees" value={stats.totalEmployees} icon="people-fill" color="info" />
                    <StatsCard title="Pending Requests" value={stats.pendingRequests} icon="hourglass-split" color="warning" />
                    <StatsCard title="Assets Under Service" value={stats.assetsUnderService} icon="tools" color="danger" />
                </div>

                <hr className="my-4" />

                <div className="row">
                    <div className="col-md-6">
                        <div className="card shadow-sm">
                            <div className="card-body">
                                <h5 className="card-title">Quick Actions</h5>
                                <p className="card-text">Manage your assets and employees with ease.</p>
                                {/* Link to a future Asset Management page */}
                                <Link to="/admin/assets" className="btn btn-primary me-2">Manage Assets</Link>
                                <Link to="/admin/employees" className="btn btn-info">Manage Employees</Link>
                            </div>
                        </div>
                    </div>
                    {/* You can add another card for recent activity here */}
                </div>
            </div>
        </MainLayout>
    );
};

export default AdminDashboard;

import React from 'react';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';
import { useAuth } from './context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

const AppNavbar = () => {
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    return (
        <Navbar bg="dark" variant="dark" expand="lg" className="shadow-sm mb-4">
            <Container fluid>
                <Navbar.Brand as={Link} to="/">Archivist</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        {/* Role-based navigation links */}
                        {user?.role === 'ROLE_ADMIN' && (
                            <Nav.Link as={Link} to="/admin-dashboard">Admin Dashboard</Nav.Link>
                        )}
                        {user?.role === 'ROLE_EMPLOYEE' && (
                            <Nav.Link as={Link} to="/employee-dashboard">My Dashboard</Nav.Link>
                        )}
                    </Nav>
                    <Nav className="ms-auto">
                        {user ? (
                            <NavDropdown title={user.email || 'Profile'} id="basic-nav-dropdown" align="end">
                                <NavDropdown.Item as={Link} to="/profile">Profile</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item onClick={handleLogout}>Logout</NavDropdown.Item>
                            </NavDropdown>
                        ) : (
                            <Nav.Link as={Link} to="/login">Login</Nav.Link>
                        )}
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default AppNavbar;
